export declare const cdsFormatOptionsSampleCode = "using {\n  Person    as User,\n  UserIdent as UserID\n} from './persons';\n\n@requires             : 'authenticated-user'  @insertonly\n@PropertyRestrictions : true                  @Searchable : true\nentity Task : managed {\n  key id       : /* UUID */ String(20) as taskId;\n      assignee :            Association to one User @cds.on.insert /*1*/ :       #user\n      priority :            Integer    as prio;\n      dueOn    :            DateTime                @cds.on.insert       : /*2*/ #now;\n}\n\ncontext Lists {\n  entity TaskList    as\n      select from Task as task\n      join ToDo as todo\n        on  todo.taskId =      't' + task.id\n        and task.dueOn  is not null\n      mixin {\n        taskType        : Association to one TaskType\n\n                            on taskType.available = true;\n        current         : Boolean = true;\n        rejectionStatus : String  = 'none';\n      }\n      into {\n        task.id : String,\n        task.dueOn    as dueOn,\n        task.assignee as assignee\n      }\n    union\n      select from GenericTasks\n      mixin {}\n      into {\n        'none' as id\n      };\n\n  entity HiddenTasks as\n    select from Hidden {\n\n\n      // only expose ID\n      id\n    }\n}\n\ntype Number : Integer;\ntype Text : String;\n";
